select 
(select count(tr.per_num) from {1}.transfer tr, {1}.position pos, {1}.subdiv sub, {1}.per_data pd where degree_id=04 and sub.code_subdiv=lpad('{0}',3,'0') and tr.subdiv_id=sub.subdiv_id and pd.per_num=TR.PER_NUM and pos.pos_id=tr.pos_id and substr(pos.code_pos,1,1)=3 and pd.retirer_sign=1 and (tr.date_transfer = (select max(date_transfer) from {1}.transfer tr2 where tr2.per_num = tr.per_num)) and tr.type_transfer_id!=3) as rtr,
(select count(tr.per_num) from {1}.transfer tr, {1}.position pos, {1}.subdiv sub, {1}.per_data pd, {1}.emp where degree_id=04 and sub.code_subdiv=lpad('{0}',3,'0') and tr.subdiv_id=sub.subdiv_id and pd.per_num=TR.PER_NUM and pos.pos_id=tr.pos_id and substr(pos.code_pos,1,1)=3 and emp.per_num=tr.per_num and upper(emp.emp_sex)='Æ' and pd.retirer_sign=1 and (tr.date_transfer = (select max(date_transfer) from {1}.transfer tr2 where tr2.per_num = tr.per_num)) and tr.type_transfer_id!=3) as rtr_w
from dual