select VL.VIOLATION_LOG_ID, VL.ARREST_DATE, VL.EXACT_DATE, VL.INFORM_SUBD_DATE, VL.TYPE_EXACT_ID, VL.TYPE_VIOLATION_ID,  VL.CAUSE_ARREST, VL.TAKE_MEASURES, VL.NOTE, VL.TYPE_STOLEN_TMC,  VL.UNIT, VL.QUANTITY_GOODS, VL.TOTAL_STOLEN, LV.PER_NUM, LV.PERCO_SYNC_ID, LV.OTHER_VIOLATOR_ID 
from {0}.VIOLATION_LOG vl, {0}.LIST_VIOLATOR lv 
where lv.per_num = {1} and LV.VIOLATION_LOG_ID = VL.VIOLATION_LOG_ID